const hextoFloat = (bin) => {
    bin = bin.padStart(32, '0');
    const sign = bin[0] === '1' ? -1 : 1;
    const exponent = parseInt(bin.slice(1, 9), 2) - 127;
    const fractionBits = bin.slice(9);
    let mantissa = 1;

    for (let i = 0; i < fractionBits.length; i++) {
        if (fractionBits[i] === '1') {
            mantissa += 1 / (2 ** (i + 1));
        }
    }

    return sign * mantissa * (2 ** exponent);
};

const combineAndConvert = (v1, v2) => {
    const hex = v1.toString(16).padStart(2, '0') + v2.toString(16).padStart(2, '0');
    const bin = parseInt(hex, 16).toString(2).padStart(32, '0');
    return parseFloat(hextoFloat(bin).toFixed(3));
};

